export function shuffleArr(arr) {
  const a = [...arr];
  for (let i = a.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [a[i], a[j]] = [a[j], a[i]];
  }
  return a;
}

export function buildWordCards(levelKey, useHiragana = false) {
  const words = WORD_DB[levelKey] || [];
  const shouldUseHiragana = useHiragana && ['eiken5', 'eiken4', 'eiken3'].includes(levelKey);

  // Convert to hiragana if needed
  const processedWords = words.map(w => ({
    ...w,
    japanese: shouldUseHiragana && HIRAGANA_MAP[w.japanese] ? HIRAGANA_MAP[w.japanese] : w.japanese
  }));

  const allJapanese = processedWords.map(w => w.japanese);
  return processedWords.map(w => {
    const wrongPool = shuffleArr(allJapanese.filter(j => j !== w.japanese));
    const wrongs = wrongPool.slice(0, 3);
    return { english: w.english, correct: w.japanese, wrongs, pos: w.pos, category: w.category, levelKey };
  });
}

export function buildWeakWordCards(stats, useHiragana = false) {
  const allCards = [];
  for (const levelKey of Object.keys(WORD_DB)) {
    const cards = buildWordCards(levelKey, useHiragana);
    for (const card of cards) {
      const stat = stats[card.english];
      if (stat && stat.wrongCount > 0) {
        allCards.push({ ...card, weakScore: stat.wrongCount });
      }
    }
  }
  return allCards.sort((a, b) => b.weakScore - a.weakScore).slice(0, 50);
}
